sys_script_include.script:
var  NeedItUtils = Class.create();
 NeedItUtils.prototype = {
    initialize: function() {
    },
	isDatePast: function(strDate){
		var rightNow = new GlideDateTime();
		var toCheck = new GlideDateTime(strDate);
		return toCheck.before(rightNow);
	},
	isToday: function(strDate){
		var rightNow = new GlideDateTime();
		var toCheck = new GlideDateTime(strDate);
		return rightNow.getLocalDate().compareTo(toCheck.getLocalDate()) == 0;
	},
    type: ' NeedItUtils'
};
