sys_script_include.script:
var NeedItWhatNeededService = Class.create();
NeedItWhatNeededService.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getWhatNeededByRequestType: function(){
		var requestType = this.getParameter('sysparm_requestType');
		var result = [];
		if(requestType == 'hr') {
			result.push({value:'hr1', text: 'Human Resources 1'});
			result.push({value:'hr2', text: 'Human Resources 2'});
			result.push({value:'other', text: 'Other'});
		}
		else if (requestType == 'facilities'){
			result.push({value:'facilities1', text: 'Facilities 1'});
			result.push({value:'facilities2', text: 'Facilities 2'});
			result.push({value:'other', text: 'Other'});
		}
		else if(requestType == 'legal'){
			result.push({value:'legal1', text: 'Legal 1'});
			result.push({value:'legal2', text: 'Legal 2'});
			result.push({value:'other', text: 'Other'});
		}
		return result.length > 0 ? JSON.stringify({success: true, data: result }) : JSON.stringify({success: false, data: result, errorMessage: 'Error loading what needed from request type.'});
	},
    type: 'NeedItWhatNeededService'
});
